<script>
function setCookie(name,value,days) {
    var expires = "";
    if (days) {
        var date = new Date();
        date.setTime(date.getTime() + (days*24*60*60*1000));
        expires = "; expires=" + date.toUTCString();
    }
    document.cookie = name + "=" + (value || "")  + expires + "; path=/";
}
function getCookie(name) {
    var nameEQ = name + "=";
    var ca = document.cookie.split(';');
    for(var i=0;i < ca.length;i++) {
        var c = ca[i];
        while (c.charAt(0)==' ') c = c.substring(1,c.length);
        if (c.indexOf(nameEQ) == 0) return c.substring(nameEQ.length,c.length);
    }
    return null;
}
function eraseCookie(name) {   
    document.cookie = name+'=; Max-Age=-99999999;';  
}
function query_url(name, url) {
    if (!url) url = window.location.href;
    name = name.replace(/[\[\]]/g, "\\$&");
    var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
        results = regex.exec(url);
    if (!results) return null;
    if (!results[2]) return '';
    return decodeURIComponent(results[2].replace(/\+/g, " "));
}
if(query_url('u')){
setCookie('redirect', query_url('u'));
}
if(query_url('url')){
setCookie('redirect', query_url('url'));
}
if(document.referrer.indexOf(location.host) > -1) {
var parser = document.createElement('a');
parser.href = document.referrer;
//parser.protocol+parser.hostname+parser.port+parser.pathname+parser.search+parser.hash+parser.host;
setCookie('referer',parser.protocol+'//'+parser.hostname+parser.pathname,1);
  } else {
setCookie('referer',document.referrer,1);
}
if(!document.referrer){
setCookie('referer',location.protocol+'//'+location.host+location.pathname+'?utm=direct',1);
}
/*___Remove Cookie Before Unload/Leaving Page___*/

var x = getCookie('referer');
var y = getCookie('redirect');
$(document).ready(function () {
$(window).unload(function() {
if (x) {eraseCookie(x);}
if (y) {eraseCookie(y);}
});
});
</script>
